import logo from './logo.svg';
import './App.css';
import Day from './Day';
import Level from './Level';
import Vocab from './Vocab';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      page_state: '1',
      level: '',
      days: '',
      words: []
    };
  }

  handleLevel = input_level => {
    this.setState({
      page_state: '2',
      level: input_level
    });
  }

  handleDays = input_days => {
