    }
    else if(page_state === '2'){
      return <Day handleButton={this.handleDays}/>
    }
    else if(page_state === '3'){
      return <Vocab handleButton={this.handleVocab}/>;
    }

    else{
      return 'error occurred';
    }
  }

  render(){
